import React, { Component } from 'react';
import FlightList from './FlightList';
import RestaurantService from '../services/RestaurantService';
import search from '../assets/logo/magnifying-glass.png';

class SearchRestaurantByName extends Component {
    constructor(props) {
        super(props);

        this.state = {
            restrauntName : this.props.restraunttName
        }
        console.log("from constructor");
        console.log(this.props.restrauntName);
        
        // this.addingTry = this.addingTry.bind(this);
        // this.visitToCart=this.visitToCart.bind(this);
    }

    componentDidMount(){
        let Owner= { restrauntName: this.state.restrauntName}
        console.log(this.state.username)
        console.log('Owner =>' + JSON.stringify(Owner));
        RestaurantService.getAllRestaurant().then((res) => {
            this.setState({ Owner: res.data })
            this.state.own = (res.data);
            console.log(this.state.own);
        })
    }


    getFlightsList = () => {
        this.setState({
            searched: false
        });
       
        RestaurantService.getRestaurantByName(this.state.restrauntName).then(data => {
            if(data.length>0){
                this.setState({
                    restaurant : data,
                    searched : true
                    
                });
                console.log("inside if");
                
            }
            else{
                alert('No Flights Found!!')
        }
            
        })
    }

    render() {
        return (
            <div className="container-fluid p-4 m-3">
                <h2 style={styling.heading}>Search By Restaurant</h2>
                <div className="form-inline">
                    <div className="input-group mb-2 mr-sm-2">
                       
                        <input type = "text" className="custom-select my-1 mr-sm-2" id="restaurantName" name="restaurantName" value={this.state.restrauntName} required/>
                    </div>       
                        
                    <button onClick={this.getFlightsList} className="btn mb-2 mr-sm-2" style={styling.icon}>
                        <img src={search} height="25" />
                    </button>
                </div>
 
                {/* {this.state.searched && <FlightList flights={this.state.flights}  />} */}
               
            </div>
        );
    }
}

let styling = {
    heading : {
        color : "#333333",
        fontFamily : "Verdana",
        marginBottom : 20
    },
    icon : {
       
    }
}

export default SearchRestaurantByName;
