import React, { Component } from 'react';
import FlightList from './FlightList';
import FlightServiceRest from '../services/';
import search from '../assets/logo/magnifying-glass.png';

class SearchFlight extends Component {
    constructor(props) {
        super(props);
        this.service = new RestaurantService();
        this.flag = false
        this.state = {
            restaurantName : "",
            searched : false
        }
    }



    componentDidMount(){
        this.setState({
            searched: false
        })
    }

    handleInput = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        this.setState({
            [name]:value
        })
    }

    
    getFlightsList = () => {
        this.setState({
            searched: false
        });
        const s = this.state.source;
        const d = this.state.destination;
        const t = this.state.travelDate;

        this.service.getFlightsForUser(s,d,t).then(data => {
            if(data.length>0){
                this.setState({
                    flights : data,
                    searched : true
                });
               
            }
            else{
                alert('No Flights Found!!')
        }
            
        })
    }

    render() {
        return (
            <div className="container-fluid p-4 m-3">
                <h2 style={styling.heading}>Search By Restaurant</h2>
                <div className="form-inline">
                    <div className="input-group mb-2 mr-sm-2">
                       
                        <input type = "text" className="custom-select my-1 mr-sm-2" id="source" name="source" onClick={this.handleInput} required/>
                    </div>       
                        
                    <button onClick={this.getFlightsList} className="btn mb-2 mr-sm-2" style={styling.icon}>
                        <img src={search} height="25" />
                    </button>
                </div>
 
                {this.state.searched && <FlightList flights={this.state.flights}  />}
               
            </div>
        );
    }
}

let styling = {
    heading : {
        color : "#333333",
        fontFamily : "Verdana",
        marginBottom : 20
    },
    icon : {
       
    }
}

export default SearchFlight;




